import * as React from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';
import axios from 'axios';
import ITutorialData from '../types/tutorial.type';

interface IState {
    tutorials: any[];
}

export default class Detail extends React.Component<RouteComponentProps, IState> {
    // CONSTRUCTOR
    constructor(props: RouteComponentProps) {
        super(props);
        this.state = { tutorials: [] }
    }

    // METHOD OR FUNCTION

    //GET
    public componentDidMount(): void {
        axios.get(`http://localhost:8080/`).then(data => {
            this.setState({ tutorials: data.data })
        })
    }

    // HTML
    public render() {
        const tutorials = this.state.tutorials;
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <table className="table table-bordered">
                            <thead className="thead-light">
                                <tr>
                                    <th scope="col">Title</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {tutorials && tutorials.map((tutorial: ITutorialData, index: number) => (
                                // <li className="list-group-item ">{tutorial.title}{tutorial.title}</li>
                                <tr>
                                    <tr key={tutorial.id}>
                                        <td>{tutorial.title}</td>
                                        <td>{tutorial.published ? "Published" : "Pending"}</td>
                                        <td>{tutorial.description}</td>
                                    </tr>
                                </tr>
                                ))}

                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
