import * as React from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';
import axios from 'axios';

interface IState {
    tutorials: any[];
}

export default class Detail extends React.Component<RouteComponentProps, IState> {
    // CONSTRUCTOR
    constructor(props: RouteComponentProps) {
        super(props);
        this.state = { tutorials: [] }
    }

    // METHOD OR FUNCTION

    //GET
    public componentDidMount(): void {
        axios.get(`http://localhost:5000/tutorials`).then(data => {
            this.setState({ tutorials: data.data })
        })
    }

    // HTML
    public render() {
        const tutorials = this.state.tutorials;
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <table className="table table-bordered">
                            <thead className="thead-light">
                                <tr>
                                    <th scope="col">Title</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Status</th>
                                    {/* <th scope="col">Phone</th>
                                    <th scope="col">Address</th>
                                    <th scope="col">Description</th> */}
                                </tr>
                            </thead>
                            <tbody>
                                {tutorials && tutorials.map(tutorial =>
                                    <tr key={tutorial.id}>
                                        <td>{tutorial.title}</td>
                                        <td>{tutorial.published ? "Published" : "Pending"}</td>
                                        <td>{tutorial.description}</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
