import { Component, ChangeEvent } from "react";
import TutorialDataService from "../services/service.service";
import ITutorialData from '../types/products.type';

type Props = {};

type State = ITutorialData & {
  submitted: boolean
};

export default class Add extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.saveTutorial = this.saveTutorial.bind(this);
    this.newTutorial = this.newTutorial.bind(this);

    this.state = {
      id: null,
      name: "",
      description: "",
      price: null,
      discounted_price: null,
      image: "",
      image_2: "",
      thumbnail: "",
      display: "",
      published: false,
      submitted: false
    };


  }

  onChangeTitle(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      name: e.target.value
    });
  }

  onChangeDescription(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      description: e.target.value
    });
  }

  saveTutorial() {
    const data: ITutorialData = {
      name: this.state.name,
      description: this.state.description,
      price: this.state.price,
      discounted_price: this.state.discounted_price,
      image: this.state.image,
      image_2: this.state.image_2,
      thumbnail: this.state.thumbnail,
      display: this.state.display,
    };

    TutorialDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          description: response.data.description,
          price: response.data.price,
          discounted_price: response.data.discounted_price,
          image: response.data.image,
          image_2: response.data.image_2,
          thumbnail: response.data.thumbnail,
          display: response.data.display,
          published: response.data.published,
          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newTutorial() {
    this.setState({
      id: null,
      name: "",
      description: "",
      price: null,
      discounted_price: null,
      image: "",
      image_2: "",
      thumbnail: "",
      display: "",
      published: false,
      submitted: false
    });
  }

  render() {
    const { submitted, name, description } = this.state;
    return (
      <div className="submit-form">
        {submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newTutorial}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                className="form-control"
                id="title"
                required
                value={name}
                onChange={this.onChangeTitle}
                name="title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={description}
                onChange={this.onChangeDescription}
                name="description"
              />
            </div>

            <button onClick={this.saveTutorial} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
