import * as React from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';
import axios from 'axios';

interface IState {
    tutorials: any[];
}

export default class Home extends React.Component<RouteComponentProps, IState> {
    // CONSTRUCTOR
    constructor(props: RouteComponentProps) {
        super(props);
        this.state = { tutorials: [] }
    }

    // METHOD OR FUNCTION

    //GET
    public componentDidMount(): void {
        axios.get(`http://localhost:8080`).then(data => {
            this.setState({ tutorials: data.data })
        })
    }

    //DELETE
    public deletetutorial(id: number) {
        axios.delete(`http://localhost:8080/tutorials/${id}`).then(data => {
            const index = this.state.tutorials.findIndex(tutorial => tutorial.id === id);
            this.state.tutorials.splice(index, 1);
            this.props.history.push('/');
        })
    }

    // HTML
    public render() {
        const tutorials = this.state.tutorials;
        return (
            <div>
                {tutorials.length === 0 && (
                    <div className="text-center">
                        <h2>No tutorial found at the moment</h2>
                    </div>
                )}
                <div className="container">
                    <div className="row">
                        <table className="table table-bordered">
                            <thead className="thead-light">
                                <tr>
                                    <th scope="col">Firstname</th>
                                    <th scope="col">Lastname</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">Phone</th>
                                    <th scope="col">Address</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {tutorials && tutorials.map(tutorial =>
                                    <tr key={tutorial.id}>
                                        <td>{tutorial.first_name}</td>
                                        <td>{tutorial.last_name}</td>
                                        <td>{tutorial.email}</td>
                                        <td>{tutorial.phone}</td>
                                        <td>{tutorial.address}</td>
                                        <td>{tutorial.description}</td>
                                        <td>
                                            <div className="d-flex justify-content-between align-items-center">
                                                <div className="btn-group" style={{ marginBottom: "20px" }}>
                                                    {/* Look at url that same exacty for this */}
                                                    <Link to={`detail/${tutorial.id}`} className="btn btn-sm btn-outline-secondary">Detail tutorial</Link>
                                                    <Link to={`edit/${tutorial.id}`} className="btn btn-sm btn-outline-secondary">Edit tutorial </Link>
                                                    <button className="btn btn-sm btn-outline-secondary" onClick={() => this.deletetutorial(tutorial.id)}>Delete tutorial</button>
                                                </div>
                                            </div>
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
