import { Component, ChangeEvent } from "react";
import TutorialDataService from "../services/service.service";
import ITutorialData from '../types/products.type';

type Props = {};

type State = ITutorialData & {
  submitted: boolean
};

export default class Add extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeDiscountedPrice = this.onChangeDiscountedPrice.bind(this);
    this.onChangeImage = this.onChangeImage.bind(this);
    this.onChangeImage2 = this.onChangeImage2.bind(this);
    this.onChangeThumbnail = this.onChangeThumbnail.bind(this);
    this.onChangeDisplay = this.onChangeDisplay.bind(this);
    this.newTutorial = this.newTutorial.bind(this);

    this.saveTutorial = this.saveTutorial.bind(this);
    this.newTutorial = this.newTutorial.bind(this);

    this.state = {
      product_id: null,
      name: "",
      description: "",
      price: null,
      discounted_price: null,
      image: "",
      image_2: "",
      thumbnail: "",
      display: "",
      // published: false,
      submitted: false
    };


  }

  onChangeName(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      name: e.target.value
    });
  }

  onChangeDescription(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      description: e.target.value
    });
  }

  onChangePrice(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      price: e.target.value
    });
  }

  onChangeDiscountedPrice(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      discounted_price: e.target.value
    });
  }

  onChangeImage(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      image: e.target.value
    });
  }

  onChangeImage2(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      image_2: e.target.value
    });
  }

  onChangeThumbnail(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      thumbnail: e.target.value
    });
  }

  onChangeDisplay(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      display: e.target.value
    });
  }
  saveTutorial() {
    const data: ITutorialData = {
      name: this.state.name,
      description: this.state.description,
      price: this.state.price,
      discounted_price: this.state.discounted_price,
      image: this.state.image,
      image_2: this.state.image_2,
      thumbnail: this.state.thumbnail,
      display: this.state.display
    };

    TutorialDataService.create(data)
      .then(response => {
        this.setState({
          product_id: response.data.product_id,
          name: response.data.name,
          description: response.data.description,
          price: response.data.price,
          discounted_price: response.data.discounted_price,
          image: response.data.image,
          image_2: response.data.image_2,
          thumbnail: response.data.thumbnail,
          display: response.data.display,
          // published: response.data.published,
          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newTutorial() {
    this.setState({
      product_id: null,
      name: "",
      description: "",
      price: null,
      discounted_price: null,
      image: "",
      image_2: "",
      thumbnail: "",
      display: "",
      // published: false,
      submitted: false
    });
  }

  render() {
    const { submitted, name, description, price, discounted_price, image, image_2, thumbnail, display } = this.state;
    return (
      <div className="submit-form">
        {submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newTutorial}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="title">Name</label>
              <input
                type="text"
                className="form-control"
                id="title"
                required
                value={name}
                onChange={this.onChangeName}
                name="title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={description}
                onChange={this.onChangeDescription}
                name="description"
              />
            </div>


            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input
                type="text"
                className="form-control"
                id="price"
                required
                value={price}
                onChange={this.onChangePrice}
                name="price"
              />
            </div>

            <div className="form-group">
              <label htmlFor="discounted_price">Discount Price</label>
              <input
                type="text"
                className="form-control"
                id="discounted_price"
                required
                value={discounted_price}
                onChange={this.onChangeDiscountedPrice}
                name="discounted_price"
              />
            </div>


            <div className="form-group">
              <label htmlFor="image">Image</label>
              <input
                type="text"
                className="form-control"
                id="image"
                required
                value={image}
                onChange={this.onChangeImage}
                name="image"
              />
            </div>

            <div className="form-group">
              <label htmlFor="image_2">Image 2</label>
              <input
                type="text"
                className="form-control"
                id="image_2"
                required
                value={image_2}
                onChange={this.onChangeImage2}
                name="image_2"
              />
            </div>

            <div className="form-group">
              <label htmlFor="thumbail">Thumbnail</label>
              <input
                type="text"
                className="form-control"
                id="thumbail"
                required
                value={thumbnail}
                onChange={this.onChangeThumbnail}
                name="thumbail"
              />
            </div>

            <div className="form-group">
              <label htmlFor="display">Display</label>
              <input
                type="text"
                className="form-control"
                id="display"
                required
                value={display}
                onChange={this.onChangeDisplay}
                name="display"
              />
            </div>

            <button onClick={this.saveTutorial} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
