import { Component, ChangeEvent } from "react";
import TutorialDataService from "../services/tutorial.service";
import { Link, RouteComponentProps } from "react-router-dom";
import ITutorialData from '../types/tutorial.type';
import { Table } from "react-bootstrap";
import axios from 'axios';

interface IState {
  customers: any[];
}
type Props = {};

type State = {
  tutorials: Array<ITutorialData>,
  currentTutorial: ITutorialData | null,
  currentIndex: number,
  searchTitle: string
};

export default class TutorialsList extends Component<Props, State>{
  constructor(props: Props, props2: RouteComponentProps) {
    super(props);
    this.onChangeSearchTitle = this.onChangeSearchTitle.bind(this);
    this.retrieveTutorials = this.retrieveTutorials.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveTutorial = this.setActiveTutorial.bind(this);
    this.removeAllTutorials = this.removeAllTutorials.bind(this);
    this.searchTitle = this.searchTitle.bind(this);
    this.state = {tutorials: [],currentTutorial: null,currentIndex: -1,searchTitle: ""};
  }


  componentDidMount() {
    this.retrieveTutorials();
  }

  onChangeSearchTitle(e: ChangeEvent<HTMLInputElement>) {
    const searchTitle = e.target.value;

    this.setState({
      searchTitle: searchTitle
    });
  }

  retrieveTutorials() {
    TutorialDataService.getAll()
      .then(response => {
        this.setState({
          tutorials: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveTutorials();
    this.setState({
      currentTutorial: null,
      currentIndex: -1
    });
  }

  setActiveTutorial(tutorial: ITutorialData, index: number) {
    this.setState({
      currentTutorial: tutorial,
      currentIndex: index
    });
  }


  //DELETE
  public deleteCustomer(id: number) {
    axios.delete(`http://localhost:8000/tutorials/${id}`).then(data => {
        const index = this.state.tutorials.findIndex(tutorial => tutorial.id === id);
        this.state.tutorials.splice(index, 1);
        //this.props.history.push('/');
    })
  }


  removeAllTutorials() {
    TutorialDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchTitle() {
    this.setState({
      currentTutorial: null,
      currentIndex: -1
    });

    TutorialDataService.findByTitle(this.state.searchTitle)
      .then(response => {
        this.setState({
          tutorials: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchTitle, tutorials, currentTutorial, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by title"
              value={searchTitle}
              onChange={this.onChangeSearchTitle}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchTitle}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Tutorials List</h4>
            {/* <ul className="list-group">
              {tutorials &&
                tutorials.map((tutorial: ITutorialData, index: number) => (
                  <li className="list-group-item ">{tutorial.title}{tutorial.title}</li>
                ))}
            </ul> */}

            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Detail</th>
                  <th>Edit</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>

                {tutorials &&
                tutorials.map((tutorial: ITutorialData, index: number) => (
                  // <li className="list-group-item ">{tutorial.title}{tutorial.title}</li>
                  <tr>
                    <td>{tutorial.id}</td>
                    <td>{tutorial.title}</td>
                    <td>{tutorial.description}</td>
                    <td>
                        <div className="d-flex justify-content-between align-items-center">
                            <div className="btn-group" style={{ marginBottom: "20px" }}>
                                {/* Look at url that same exacty for this */}
                                <Link to={`detail/${tutorial.id}`} className="btn btn-sm btn-outline-secondary">Detail</Link>
                                <Link to={`edit/${tutorial.id}`} className="btn btn-sm btn-outline-secondary">Edit </Link>
                                <button className="btn btn-sm btn-outline-secondary" onClick={() => this.deleteCustomer(tutorial.id)}>Delete Customer</button>
                            </div>
                        </div>
                    </td>
                  </tr>
                ))}

              </tbody>
            </Table>


            {/* {tutorial ? (
              <Link to={"/tutorials/" + tutorial.id}>Detail</Link>
            ): (
              <div>
                <br />
                <p></p>
              </div>
            )} */}

          <button
            className="m-3 btn btn-sm btn-danger"
            onClick={this.removeAllTutorials}
          >
            Remove All
          </button>
        </div>
        {/* <div className="col-md-6">
          {currentTutorial ? (
            <div>
              <h4>Tutorial</h4>
              <div>
                <label>
                  <strong>Title:</strong>
                </label>{" "}
                {currentTutorial.title}
              </div>

              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentTutorial.description}
              </div>

              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentTutorial.published ? "Published" : "Pending"}
              </div>

              <Link to={"/tutorials/" + currentTutorial.id}>Edit</Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Tutorial...</p>
            </div>
          )}
        </div> */}
      </div>
    );
  }
}
