import { Component, ChangeEvent } from "react";
import TutorialDataService from "../services/tutorial.service";
import ITutorialData from '../types/tutorial.type';
import { Table } from "react-bootstrap";
import axios from 'axios';
import * as React from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';

interface IState {
    tutorials: any[];
}

export default class Home extends React.Component<RouteComponentProps, IState> {
    // CONSTRUCTOR
    constructor(props: RouteComponentProps) {
        super(props);
        this.state = { tutorials: [] }
    }

    // METHOD OR FUNCTION

    //GET
    public componentDidMount(): void {
        axios.get(`http://localhost:8080/`).then(data => {
            this.setState({ tutorials: data.data })
        })
    }

    //DELETE
    public deletetutorial(id: number) {
        axios.delete(`http://localhost:8080/tutorials/${id}`).then(data => {
            const index = this.state.tutorials.findIndex(tutorial => tutorial.id === id);
            this.state.tutorials.splice(index, 1);
            this.props.history.push('/');
        })
    }

    // HTML
    public render() {
        const tutorials = this.state.tutorials;
        return (
            <div>
                {tutorials.length === 0 && (
                    <div className="text-center">
                        <h2>No tutorial found at the moment</h2>
                    </div>
                )}
                <div className="container">
                    <div className="row">
                        <table className="table table-bordered">
                            <thead className="thead-light">
                                <tr>
                                    <th scope="col">ID</th>
                                    <th scope="col">Title</th>
                                    <th scope="col">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                            {/* {tutorials && tutorials.map((tutorial: ITutorialData, index: number) => */}
                                {tutorials && tutorials.map(tutorial =>
                                    <tr key={tutorial.id}>
                                        <td>{tutorial.id}</td>
                                        <td>{tutorial.title}</td>
                                        <td>
                                            <div className="d-flex justify-content-between align-items-center">
                                                <div className="btn-group" style={{ marginBottom: "20px" }}>
                                                    {/* Look at url that same exacty for this */}
                                                    <Link to={`detail/${tutorial.id}`} className="btn btn-sm btn-outline-secondary">Detail</Link>
                                                    <Link to={`edit/${tutorial.id}`} className="btn btn-sm btn-outline-secondary">Edit </Link>
                                                    <button className="btn btn-sm btn-outline-secondary" onClick={() => this.deletetutorial(tutorial.id)}>Delete</button>
                                                </div>
                                            </div>
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
