import { Component, ChangeEvent } from "react";
import TutorialDataService from "../services/service.service";
import ITutorialData from '../types/products.type';

type Props = {};

type State = ITutorialData & {
  submitted: boolean
};

export default class Add extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeDiscountedPrice = this.onChangeDiscountedPrice.bind(this);
    this.onChangeImage = this.onChangeImage.bind(this);
    this.onChangeImage2 = this.onChangeImage2.bind(this);
    this.onChangeThumbnail = this.onChangeThumbnail.bind(this);
    this.onChangeDisplay = this.onChangeDisplay.bind(this);
    this.newTutorial = this.newTutorial.bind(this);

    this.saveTutorial = this.saveTutorial.bind(this);
    this.newTutorial = this.newTutorial.bind(this);

    this.state = {
      idproduct: null,
      name: "",
      artist: "",
      year: "",
      datepost: "",
      datestart: "",
      dateend: "",
      numberallowed: null,
      venue: "",
      description: "",
      image: "",
      location_image: "",
      video: "",
      location_video: "",
      time: "",
      qrCodeImage: "",
      whoisposted: "",
      submitted: false,
    };


  }

  onChangeName(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      name: e.target.value
    });
  }

  onChangeDescription(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      description: e.target.value
    });
  }

  onChangePrice(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      price: e.target.value
    });
  }

  onChangeDiscountedPrice(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      discounted_price: e.target.value
    });
  }

  onChangeImage(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      image: e.target.value
    });
  }

  onChangeImage2(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      image_2: e.target.value
    });
  }

  onChangeThumbnail(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      thumbnail: e.target.value
    });
  }

  onChangeDisplay(e: ChangeEvent<HTMLInputElement>) {
    this.setState({
      display: e.target.value
    });
  }
  saveTutorial() {
    const data: ITutorialData = {
      name: this.state.name,
      artist: this.state.artist,
      year: this.state.year,
      datepost: this.state.datepost,
      datestart: this.state.datestart,
      dateend: this.state.dateend,
      numberallowed: this.state.numberallowed,
      venue: this.state.venue,
      description: this.state.description,
      image: this.state.image,
      location_image: this.state.location_image,
      video: this.state.video,
      location_video: this.state.location_video,
      time: this.state.time,
      qrCodeImage: this.state.qrCodeImage,
      whoisposted: this.state.whoisposted
    };

    TutorialDataService.create(data)
      .then(response => {
        this.setState({
          name: response.data.name,
          artist: response.data.artist,
          year: response.data.year,
          datepost: response.data.datepost,
          datestart: response.data.datestart,
          dateend: response.data.dateend,
          numberallowed: response.data.numberallowed,
          venue: response.data.venue,
          description: response.data.description,
          image: response.data.image,
          location_image: response.data.location_image,
          video: response.data.video,
          location_video: response.data.location_video,
          time: response.data.time,
          qrCodeImage: response.data.qrCodeImage,
          whoisposted: response.data.whoisposted
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newTutorial() {
    this.setState({
      idproduct: null,
      name: "",
      artist: "",
      year: "",
      datepost: "",
      datestart: "",
      dateend: "",
      numberallowed: null,
      venue: "",
      description: "",
      image: "",
      location_image: "",
      video: "",
      location_video: "",
      time: "",
      qrCodeImage: "",
      whoisposted: "",
      submitted: false,
    });
  }

  render() {
    const { 
      name,
      artist,
      year,
      datepost,
      datestart,
      dateend,
      numberallowed,
      venue,
      description,
      image,
      location_image,
      video,
      location_video,
      time,
      qrCodeImage,
      whoisposted,
      submitted,
    } = this.state;
    return (
      <div className="submit-form">
        {submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newTutorial}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                required
                value={name}
                onChange={this.onChangeName}
                name="name"
              />
            </div>

            <div className="form-group">
              <label htmlFor="artist">Artist</label>
              <input
                type="text"
                className="form-control"
                id="artist"
                required
                value={artist}
                onChange={this.onChangeArtist}
                name="artist"
              />
            </div>


            <div className="form-group">
              <label htmlFor="year">Year</label>
              <input
                type="text"
                className="form-control"
                id="year"
                required
                value={year}
                onChange={this.onChangeYear}
                name="year"
              />
            </div>

            <div className="form-group">
              <label htmlFor="datepost">Date Post</label>
              <input
                type="text"
                className="form-control"
                id="datepost"
                required
                value={datepost}
                onChange={this.onChangeDatepost}
                name="datepost"
              />
            </div>


            <div className="form-group">
              <label htmlFor="datestart">Date Start</label>
              <input
                type="text"
                className="form-control"
                id="datestart"
                required
                value={datestart}
                onChange={this.onChangeDatestart}
                name="datestart"
              />
            </div>

            <div className="form-group">
              <label htmlFor="dateend">Date End</label>
              <input
                type="text"
                className="form-control"
                id="dateend"
                required
                value={dateend}
                onChange={this.onChangeDateend}
                name="dateend"
              />
            </div>

            <div className="form-group">
              <label htmlFor="numberallowed">Number Allowed</label>
              <input
                type="text"
                className="form-control"
                id="numberallowed"
                required
                value={numberallowed}
                onChange={this.onChangeNumberallowed}
                name="numberallowed"
              />
            </div>

            <div className="form-group">
              <label htmlFor="venue">Venue</label>
              <input
                type="text"
                className="form-control"
                id="venue"
                required
                value={venue}
                onChange={this.onChangeVenue}
                name="venue"
              />
            </div>

            <button onClick={this.saveTutorial} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
