import { Component, ChangeEvent } from "react";
import { RouteComponentProps } from 'react-router-dom';

import TutorialDataService from "../services/service.service";
import ITutorialData from "../types/products.type";

interface RouterProps { // type for `match.params`
  product_id: string; // must be type `string` since value comes from the URL
}

type Props = RouteComponentProps<RouterProps>;

type State = {
  currentTutorial: ITutorialData;
  message: string;
}

export default class Tutorial extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeDescriptionPrice = this.onChangeDescriptionPrice.bind(this);
    this.onChangeImage = this.onChangeImage.bind(this);
    this.onChangeImage2 = this.onChangeImage2.bind(this);
    this.onChangeThumail = this.onChangeThumail.bind(this);
    this.onChangeDisplay = this.onChangeDisplay.bind(this);
    this.getTutorial = this.getTutorial.bind(this);

    this.state = {
      currentTutorial: {
        product_id: null,
        name: "",
        description: "",
        price: null,
        discounted_price: null,
        image: "",
        image_2: "",
        thumbnail: "",
        display: "",
        // published: false,
      },
      message: "",
    };
  }

  componentDidMount() {
    this.getTutorial(this.props.match.params.product_id);
  }

  onChangeName(e: ChangeEvent<HTMLInputElement>) {
    const name = e.target.value;

    this.setState(function (prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          name: name,
        },
      };
    });
  }

  onChangeDescription(e: ChangeEvent<HTMLInputElement>) {
    const description = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        description: description,
      },
    }));
  }


  onChangePrice(e: ChangeEvent<HTMLInputElement>) {
    const price = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        price: price,
      },
    }));
  }


  onChangeDescriptionPrice(e: ChangeEvent<HTMLInputElement>) {
    const description_price = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        description_price: description_price,
      },
    }));
  }


  onChangeImage(e: ChangeEvent<HTMLInputElement>) {
    const image = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        image: image,
      },
    }));
  }


  onChangeImage2(e: ChangeEvent<HTMLInputElement>) {
    const image2 = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        image2: image2,
      },
    }));
  }


  onChangeThumail(e: ChangeEvent<HTMLInputElement>) {
    const thumbnail = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        thumbnail: thumbnail,
      },
    }));
  }


  onChangeDisplay(e: ChangeEvent<HTMLInputElement>) {
    const display = e.target.value;

    this.setState((prevState) => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        display: display,
      },
    }));
  }

  getTutorial(product_id: string) {
    TutorialDataService.get(product_id)
      .then((response) => {
        this.setState({
          currentTutorial: response.data,
        });
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  render() {
    const { currentTutorial } = this.state;

    return (
      // <div>
      //   {currentTutorial ? (
      //     <div className="edit-form">
      //       <h3>Shopping</h3>
      //       <form>
      //         <div className="form-group">
      //           <label htmlFor="name">Name</label>
      //           <p>{currentTutorial.name}</p>
      //         </div>
      //         <div className="form-group">
      //           <label htmlFor="description">Description</label>
      //           <p>{currentTutorial.description}</p>
      //         </div>

      //         <div className="form-group">
      //           <label>
      //             <strong>Status:</strong>
      //           </label>
      //           {/* {currentTutorial.published ? "Published" : "Pending"} */}
      //         </div>
      //       </form>
      //     </div>
      //   ) : (
      //     <div>
      //       <br />
      //       <p>Please click on a Tutorial...</p>
      //     </div>
      //   )}
      // </div>


      <div className="container mt-5 mb-5">
    <div className="row d-flex justify-content-center">
        <div className="col-md-10">
            <div className="card">
                <div className="row">
                    <div className="col-md-6">
                        <div className="images p-3">
                            <div className="text-center p-4"> <img id="main-image" src="https://i.imgur.com/Dhebu4F.jpg" width="250" /> </div>
                            <div className="thumbnail text-center"> 
                              {/* <img onClick="change_image(this)" src="https://i.imgur.com/Rx7uKd0.jpg" width="70"/> 
                              <img onClick="change_image(this)" src="https://i.imgur.com/Dhebu4F.jpg" width="70"/>  */}
                            </div>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="product p-4">
                            <div className="d-flex justify-content-between align-items-center">
                                <div className="d-flex align-items-center"> 
                                <i className="fa fa-long-arrow-left"></i> 
                                <span className="ml-1">Back</span> </div> 
                                <i className="fa fa-shopping-cart text-muted"></i>
                            </div>
                            <div className="mt-4 mb-3"> <span className="text-uppercase text-muted brand">Orianz</span>
                                <h5 className="text-uppercase">Men's slim fit t-shirt</h5>
                                <div className="price d-flex flex-row align-items-center"> <span className="act-price">$20</span>
                                    <div className="ml-2"> <small className="dis-price">$59</small> <span>40% OFF</span> </div>
                                </div>
                            </div>
                            <p className="about">Shop from a wide range of t-shirt from orianz. Pefect for your everyday use, you could pair it with a stylish pair of jeans or trousers complete the look.</p>
                            <div className="sizes mt-5">
                                <h6 className="text-uppercase">Size</h6> 
                                <label className="radio"> 
                                <input type="radio" name="size" value="S" checked/> <span>S</span> 
                                </label> 

                                <label className="radio"> <input type="radio" name="size" value="M"/> <span>M</span> </label> 
                                <label className="radio"> <input type="radio" name="size" value="L"/> <span>L</span> </label> 
                                <label className="radio"> <input type="radio" name="size" value="XL"/> <span>XL</span> </label> 
                                <label className="radio"> <input type="radio" name="size" value="XXL"/> <span>XXL</span> </label>
                            </div>
                            <div className="cart mt-4 align-items-center"> <button className="btn btn-danger text-uppercase mr-2 px-4">Add to cart</button> <i className="fa fa-heart text-muted"></i> <i className="fa fa-share-alt text-muted"></i> </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
    );
  }
}
